    ;

let game = {};
const revealedClassName = 'revealed';
const playedClassName = 'played';
const masterClassName = 'master';
const cardClassName = 'card';
const redCard = 'red';
const blueCard = 'blue';


function getGameNumberElement() {
    return document.getElementById('game-number');
}

function getGameBoardElement() {
    return document.getElementById('game-board');
}

function getStartGameButton() {
    return document.getElementById('start-game');
}

function getEndGameButton() {
    return document.getElementById('end-game');
}

function getStartAsAMasterButton() {
    return document.getElementById('start-as-master');
}

function getTeamIndicatorElement() {
    return document.getElementById('team-indicator');
}

function getGameResultElement() {
    return document.getElementById('game-result');
}

function toggleNavigationControlsAvailability(isDisabled) {
    let controls = document.querySelectorAll('.nav');
    for (let control of controls) {
        control.disabled = isDisabled;
    }
}

function startGame(startAsMaster = false) {
    const gameNumberFiledValue = getGameNumberElement().value;
    const gameNumber = parseInt(gameNumberFiledValue, 10) - 1;
    game = {
        number: gameNumber,
        revealed: [],
        startAsMaster: startAsMaster
    };

    drawABoard();
    updateTeamIndicator();
    disableControls();
    saveGame();
}

function disableControls() {
    toggleNavigationControlsAvailability(true);
    getStartGameButton().style.display = 'none';
    getStartAsAMasterButton().style.display = 'none';
    getEndGameButton().style.display = 'inline';
}

function enableControls() {
    getGameBoardElement().innerHTML = '';
    toggleNavigationControlsAvailability(false);
    getStartGameButton().style.display = 'inline';
    getStartAsAMasterButton().style.display = 'inline';
    getEndGameButton().style.display = 'none';
}

function previousGame() {
    getGameNumberElement().stepDown();
}

function nextGame() {
    getGameNumberElement().stepUp();
}

function updateTeamIndicator() {
    getTeamIndicatorElement().className = game.teamStarts;
}

function drawABoard() {
    let gameHtml = '';
    let countOfReds = 0;
    let countOfBlues = 0;
    for (let i = 0; i < 25; i++) {
        let cardCssClasses = ['card'];
        let currentElement = games[game.number][i];
        let isCarClicked = game.revealed.includes(i);

        if (game.startAsMaster) {
            cardCssClasses.push(revealedClassName, masterClassName);
        }

        if (isCarClicked) {
            cardCssClasses.push(game.startAsMaster ? playedClassName : revealedClassName);
        }

        let onClickAction = game.startAsMaster ? markAsPlayed.name : revealCard.name;

        gameHtml +=
            `<div class="${cardCssClasses.join(' ')}" data-reverse="${currentElement[0]}" onclick="${onClickAction}(this,${i})">
            <div class="top-text">${currentElement[1]}</div>
            <div class="bottom-text">${currentElement[1]}</div>
        </div>`;

        switch (currentElement[0]) {
            case redCard:
                countOfReds++;
                break;
            case blueCard:
                countOfBlues++;
                break;
        }
    }
    game.teamStarts = (countOfReds > countOfBlues) ? redCard : blueCard;
    getGameBoardElement().innerHTML = gameHtml;
}

function revealCard(card, index) {
    const cardAlreadyRevealed = game.revealed.includes(index);
    if (cardAlreadyRevealed) return;

    card.classList.add(revealedClassName);
    game.revealed.push(index);
    saveGame();
    checkIfGameOver();
}

function checkIfGameOver() {
    let gameBoard = games[game.number];
    let allRedCardsNotRevealed = true;
    let allBlueCardsNotRevealed = true;
    for (let i = 0; i < 25; i++) {
        if (game.revealed.includes(i)) continue;
        cardType = gameBoard[i][0];

        if (cardType === redCard) {
            allRedCardsNotRevealed = false;
        }
        if (cardType === blueCard) {
            allBlueCardsNotRevealed = false;
        }
    }

    let teamWon = '';
    if (allRedCardsNotRevealed) {
        teamWon = redCard;
    }
    if (allBlueCardsNotRevealed) {
        teamWon = blueCard;
    }
    gameResultUpdate(teamWon);
}

function gameResultUpdate(winner) {
    getGameResultElement().className = winner;
}

function markAsPlayed(card, cardPosition) {
    const index = game.revealed.indexOf(cardPosition);
    const isCardPlayed = index > -1;

    if (isCardPlayed) {
        card.classList.remove(playedClassName);
        game.revealed.splice(index, 1);
    } else {
        game.revealed.push(cardPosition);
        card.classList.add(playedClassName);
    }

    saveGame();
    checkIfGameOver();
}

function endGame() {
    game = {};
    localStorage.clear();
    getTeamIndicatorElement().className = '';
    enableControls();
    gameResultUpdate('');
}

function saveGame() {
    localStorage.setItem('game', JSON.stringify(game));
}

function restoreGame() {
    let savedGame = localStorage.getItem('game');
    if (!savedGame) return;
    game = JSON.parse(savedGame);
    getGameNumberElement().value = game.number + 1;
    drawABoard();
    updateTeamIndicator();
    disableControls();
    checkIfGameOver();
}

document.addEventListener("DOMContentLoaded", restoreGame);